    /**
     * Get Delivery Confirmation Certificate
     * @param pin is tracking number/pin
     * @return
     */
    public String getDeliveryConfirmationCertificate(String pin) {
        String[] cmds ={"curl","--location","--request","GET",
                "https://soa-gw.canadapost.ca/vis/certificate/"+pin,
                "--header","Accept: application/vnd.cpc.track-v2+xml",
                "--header","Accept-language: en-CA",
                "--header","Authorization: Basic "+k};
        return Curl.execCurl(cmds);
    }
    public Tracking getTracking(String strXML) throws DocumentException {
        Tracking tracking = new Tracking();
        Document doc = null;
        if(strXML==null || strXML.isBlank() || strXML.isEmpty()){
            return tracking;
        }else {
            doc = DocumentHelper.parseText(strXML);
            Element root = doc.getRootElement();
            Iterator it = root.elementIterator();
            while (it.hasNext()) {
                Element e = (Element) it.next();
                if(e.getName().equals("filename")){
                    tracking.setFilename(e.getTextTrim());
                }else if(e.getName().equals("image")){
                    tracking.setImage(e.getTextTrim());
                }else if(e.getName().equals("mime-type")){
                    tracking.setMimeType(e.getTextTrim());
                }
            }
        }
        return tracking;
    }
